function createContainer(parentQuery = "body", tagName = "div") {
    let container = document.createElement(tagName);
    container.classList.add("vid-container", "w-full", "mx-2", "sm:mx-0", "mt-4", "rounded-lg");
    container.setAttribute("allowfullscreen", "");
    container.style.background = "black"
    container.style.aspectRatio = "16/9"
    container.style.overflow = "hidden"
    container.style.maxHeight = "min(70vh, calc(100vh - 40px - 4rem))";
    container.style.outline = `${CONFIG.debug*1}rem solid cyan`;

    // Add to HTML
    let parent = document.querySelector(parentQuery);
    parent.append(container);

    return container;
}

(async function main() {
    // CSS
    document.head.insertAdjacentHTML("beforeend", "<link rel=\"stylesheet\" href=\"https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.4.2/css/all.min.css\">");
    document.head.insertAdjacentHTML("beforeend", "<link rel=\"stylesheet\" href=\"https://cdn.jsdelivr.net/npm/notyf@3/notyf.min.css\">");
    document.head.insertAdjacentHTML("beforeend", `<style>${CONFIG.customCss}</style>`);

    // Disable Hls
    unsafeWindow.Hls = new Proxy(class {}, {
        construct(target, args) {
            return new Proxy({}, {
                get() {
                    return () => true;
                }
            });
        },
        get(target, prop) {
            return () => true;
        }
    });

    // Replace logo
    replaceLogo();

    // DMCA troll
    document.querySelector("footer .pt-2.justify-between.sm\\:flex")?.insertAdjacentHTML("beforebegin", `<img class="pt-2" alt="DMCA troll" src="//images.dmca.com/Badges/dmca-badge-w150-5x1-01.png">`)

    // Footer credit
    document.querySelector("footer .pt-2.justify-between.sm\\:flex")?.insertAdjacentHTML("afterend", `<p class="pt-2">${CONFIG.name} v${CONFIG.version} | Được viết bởi ${CONFIG.author}</p>`)

    // Debug detect
    try {
        GM_addValueChangeListener?.("DEBUG", (key, oldValue, newValue, remote) => {
            CONFIG.debug = newValue;

            replaceLogo();
            Object.keys(blobURLMap).forEach(key => delete blobURLMap[key]);

            if (window.container) {
                window.container.style.outline = `${CONFIG.debug*1}rem solid cyan`;
            }
        });
    }
    catch(e) {}

    // Inject/replace player
    if (location.hostname.includes("opstream") || location.hostname.includes("streamc") || location.hostname.includes("player.phimapi")) {
        document.body.parentElement.style.background = "black";

        // Create video container
        let container = document.createElement("div");
        container.classList.add("vid-container");
        container.style = "position: absolute; left: 0; right: 0; top: 0; bottom: 0; width: 100%; height: 100%;";

        // Add to HTML
        document.body.replaceChildren(container);

        // Init player
        window.player = await createArtplayer();

        changeUrl(location.href);
    }
    else if ((location.hostname.includes("kkphim") || location.hostname === "216.180.226.222") && (location.pathname.startsWith("/index.php/vod/detail/") || location.pathname.startsWith("/phim/"))) {
        document.querySelectorAll("#list_episode ~ * > button").forEach(elem => elem.click());

        let epsListParentQuery = "#list_episode > div:nth-child(2)"

        window.container = createContainer(epsListParentQuery, "div");
        window.container.style.display = "none";

        window.player = await createArtplayer();

        initEpsListOnClick(epsListParentQuery, (url) => {
            if (window.player.controls.noadserror) {
                window.player.controls.remove("noadserror")
            }

            notyf.dismissAll();
            notyf.open({
                type: "info",
                message: CONFIG.betWarning
            });

            window.container.style.display = ""
            changeUrl(url)
        });
    }
    else if (location.hostname.includes("nguonc")) {
        detectEpisodeList("#content", "#list_episode > div:nth-child(2)");
    }
    else if (location.hostname.includes("ophim")) {
        detectEpisodeList(".container", ".mt-0 > div[id^=headlessui-disclosure-panel] > div");
    }
})();

// Toggle
document.addEventListener("keydown", (event) => {
    window.typed ??= "";
    window.typed = window.typed.slice(-99) + event.key.toUpperCase();

    if (window.typed.endsWith("FLASH")) {
        CONFIG.flash = !CONFIG.flash;

        requestAnimationFrame(function switchMode() {
            if (CONFIG.flash) {
                document.body.parentElement.style.filter = "invert(1)";
                requestAnimationFrame(() => {
                    document.body.parentElement.style.filter = "";
                    requestAnimationFrame(switchMode);
                });
            }
        });
    }
    if (window.typed.endsWith("DEBUG")) {
        GM_setValue("DEBUG", !CONFIG.debug)
    }
});
